import React, {Component} from 'react';
import axios from 'axios';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, CardFooter , CardHeader
  } from 'reactstrap';

export default class ProductList extends Component {

    constructor(props) {
        super(props);
        this.state = {products: []}
    }

    componentDidMount() {
        axios.get('http://localhost:4000/product/view')
             .then(response => {
                 this.setState({products: response.data});
                 console.log()
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    render() {
        return (
            <div>
                { 
                    this.state.products.map((currentUser, i) => {
                        console.log({currentUser});
                        if({currentUser}===localStorage.getItem('user')){
                            return (
                            <Card className="p-3 text-center">
                                    <CardHeader className="blockquote mb-0">{currentUser.name}</CardHeader>
                                    <CardBody className="mb-0">Quantity:{currentUser.quantity}
                                    <br/>
                                    Price:{currentUser.price}
                                    </CardBody>
                                </Card>
                            )
                        }
                    })
                }
            </div>
        )
    }
}
