import React, {Component} from 'react';
import axios from 'axios';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, CardFooter , CardHeader
  } from 'reactstrap';

export default class ProductList extends Component {

    constructor(props) {
        super(props);
        this.state = {products: []}
    }

    componentDidMount() {
        axios.get('http://localhost:4000/product/view')
             .then(response => {
                 this.setState({products: response.data});
                 console.log()
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    render() {
        return (
            <div>
                { 
                    this.state.products.map((currentUser, i) => {
                        return (
                            <Card bg="primary">
                                <CardHeader className="blockquote mb-0">{currentUser.name}</CardHeader>
                                <hr>
                                <CardTitle className="mb-0">Quantity:{currentUser.quantity}</CardTitle>
                                <CardFooter>Price:{currentUser.price}</CardFooter>
                                </hr>
                            </Card>
                        )
                    })
                }
            </div>
        )
    }
}
