import React, {Component , useState} from 'react';
import axios from 'axios';
import {
    Card, CardHeader ,Button , Collapse
  } from 'reactstrap';
export default class PlaceOrder extends Component {

    constructor(props) {
        super(props);
        this.state = {products: []}
        this.order = this.order.bind(this);
        this.toggle = this.toggle.bind(this);
        this.isOpen = false;
    }

    order(event){
        console.log("hi");
    }
    toggle(event){
        console.log(event);
        // event.target.value = !event.target.value;
    }
    componentDidMount() {
        axios.get('http://localhost:4000/product/view')
             .then(response => {
                 this.setState({products: response.data});
                 console.log()
             })
             .catch(function(error) {
                 console.log(error);
             })
    }
    render() {
        return (
            <div>
                { 
                    this.state.products.map((currentUser, i) => {
                        return (
                        <Card className="p-3 text-center">
                                <CardHeader className="blockquote mb-0">{currentUser.name} x{currentUser.quantity}</CardHeader>
                                <CardHeader>Price per unit: {currentUser.price}
                                <br/>Status: {currentUser.status}
                                <br />Vendor: {currentUser.owner}
                                </CardHeader>
                                <Button color="primary" onClick={this.toggle}>Place Order</Button>
                                <Collapse isOpen={this.toggle} value={this.isOpen}>
                                hi there
                                </Collapse>
                            </Card>
                        )
                    })
                }
            </div>
        )
    }
}
