import React, {Component} from 'react';
import axios from 'axios';
import {
    Card, CardHeader ,Button
  } from 'reactstrap';

export default class PlaceOrder extends Component {

    constructor(props) {
        super(props);
        this.state = {products: []}
        this.order = this.order.bind(this);
    }

    order(event){
        console.log("hi");
    }

    componentDidMount() {
        axios.get('http://localhost:4000/product/view')
             .then(response => {
                 this.setState({products: response.data});
                 console.log()
             })
             .catch(function(error) {
                 console.log(error);
             })
    }
    render() {
        return (
            <div>
                { 
                    this.state.products.map((currentUser, i) => {
                        return (
                        <Card className="p-3 text-center">
                                <CardHeader className="blockquote mb-0">{currentUser.name} x{currentUser.quantity}</CardHeader>
                                <CardHeader>Price per unit: {currentUser.price}
                                <br/>Status: {currentUser.status}
                                <br />Vendor: {currentUser.owner}
                                </CardHeader>
                                <Button color="primary" onClick={this.order}>Place Order</Button>
                            </Card>
                        )
                    })
                }
            </div>
        )
    }
}
