import React, {Component} from 'react';
import axios from 'axios';
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, CardFooter , CardHeader ,Button
  } from 'reactstrap';

export default class ProductList extends Component {

    constructor(props) {
        super(props);
        this.state = {products: []}
        this.remove = this.remove.bind(this);

    }
    remove(event){
        axios.get('http://localhost:4000/product/delete')
                .then(res => console.log(res.data));
    }

    componentDidMount() {
        axios.get('http://localhost:4000/product/view')
             .then(response => {
                 this.setState({products: response.data});
                 console.log()
             })
             .catch(function(error) {
                 console.log(error);
             })
    }


    render() {
        return (
            <div>
                { 
                    this.state.products.map((currentUser, i) => {
                        if(currentUser['owner']===localStorage.getItem('user')){
                            return (
                            <Card className="p-3 text-center">
                                    <CardHeader className="blockquote mb-0">{currentUser.name} x{currentUser.quantity}</CardHeader>
                                    <CardHeader>Price per unit:{currentUser.price}
                                    <br/>Status:{currentUser.status}
                                    </CardHeader>
                                    <Button color="info">Dispatch</Button>
                                    <Button color="danger" onClick={this.remove}>Remove</Button>
                                </Card>
                            )
                        }
                    })
                }
            </div>
        )
    }
}
